.home {
  display: flex;
  flex-direction: row;
  width: 100%;
}

.home__left {
  background-color: var(--light-gray-background);
  width: 310px;
  height: 100vh;
  display: flex;
  flex-direction: column;
  position: sticky;
  top: 0;
}

.home__nav {
  display: block;
  padding: 22px 22px 7px 0;
  font-weight: var(--font-weight-normal);
  font-size: var(--font-size-smaller);
  line-height: var(--font-height-smaller);
  text-align: right;
  color: var(--gray-text);
  transition: all 0.25s;
  text-decoration: none;
}

.home__nav:hover {
  cursor: pointer;
  color: var(--black-text);
}

.home__nav::after {
  content: url("/e6a7e653f785eeeb2598241a5d926e5b.svg");
  position: relative;
  left: 6px;
}

/***************************************************************   */

/* Поисковая строка */

.home__searchbox {
  margin: 0 10px;
  padding: 14px 0;
  border-bottom: 1px solid #eaeaea;
}

.searchbox {
  box-sizing: border-box;
  width: 100%;
  height: 32px;
  background: #efefef;
  border: none;
  border-radius: 5px;
  padding: 0 10px 0 28px;
  font-style: normal;
  font-weight: var(--font-weight-normal);
  font-size: var(--font-size-smaller);
  line-height: var(--font-height-smaller);
}

.home__searchbox::after {
  content: url("/8e3b6535b4ee84958b502bcb89e885b1.svg");
  position: relative;
  top: -24px;
  left: 10px;
}

.searchbox:focus {
  outline: 0;
  outline-offset: 0;
  opacity: 0.8;
}

/***************************************************************   */

/* Список чатов */

.messages-list {
  list-style: none;
  margin: 0;
  padding: 0;
  overflow-y: scroll;
}

/***************************************************************   */

/* Сниппет */

.snippet {
  box-sizing: border-box;
  height: 70px;
  margin: 0;
  transition: all 0.25s;
}

.snippet_active {
  background-color: var(--chat-outbound);
  cursor: auto;
}

.snippet:not(.snippet_active):hover {
  background-color: var(--chat-outbound);
  cursor: pointer;
}

.snippet__body {
  display: grid;
  grid-template-columns: 50px auto 25px;
  column-gap: 10px;
  grid-template-areas:
    "A B D"
    "A C E";
  margin: 0 10px;
  padding: 12px 0;
  border-bottom: 1px solid #eaeaea;
}

.snippet__avatar {
  grid-area: A;
  width: 47px;
  height: 47px;
  border-radius: 47px;
  background-color: var(--dark-gray-background);
}

.snippet__title {
  grid-area: B;
  font-weight: var(--font-weight-normal);
  font-size: var(--font-size-normal);
  line-height: var(--font-height-normal);
  color: var(--black-text);
  margin: 0 0 3px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 125px;
}

.snippet__text {
  grid-area: C;
  font-weight: var(--font-weight-normal);
  font-size: var(--font-size-smaller);
  line-height: 15px;
  color: var(--gray-text);
  overflow: hidden;
  text-overflow: ellipsis;
  display: box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
}

.snippet__text_my::before {
  content: "Вы: ";
  color: var(--black-text);
}

.snippet__time {
  grid-area: D;
  font-weight: var(--font-weight-normal);
  font-size: 9px;
  line-height: 13px;
  text-align: right;
  color: var(--dark-gray-text);
  justify-self: end;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 65px;
}

.snippet__count {
  grid-area: E;
  width: 20px;
  height: 20px;
  border-radius: 20px;
  background-color: var(--primary-blue);
  font-weight: var(--font-weight-normal);
  font-size: 11px;
  line-height: 18px;
  text-align: center;
  color: #fff;
  justify-self: end;
}

